import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
export class Home extends React.Component{

    constructor(props) {
        super(props);
        this.state = { 
            name: "Suraj",
        description : "If youre here, youre probably pretty well read. Congratulations you! Still, its one thing to read a great book, but quite another to retain it. Especially considering most of us read a number of the classics in our teenage years, when we were only half paying attention anyway. And its one thing to know that Shakespeare wrote something as the sages say: when in doubt, its from Shakespeare, but quite another to remember the play it came from.."
    };
    this.handleName= this.handleName.bind(this);
    this.handleD= this.handleD.bind(this);
    }
    handleName(event){
        this.setState({name: event.target.value});
        event.preventDefault();
    }
    handleD(event){
        this.setState({description: event.target.value});
        event.preventDefault();
    }

    render(){
        return(
            <div class="card mb-3" max-width="540px">
                <div className="card">
                    <div class="card mb-3" width="700">
                    <div className="row">
                    <div class="row g-0">
                        <div class="col-md-4">
                                     <img src="https://thumbs.dreamstime.com/b/black-hacker-software-engineer-symbol-white-background-concept-depicting-danger-using-internet-protection-co-117453507.jpg" class="img-fluid rounded-start" alt="" width="300" height="300"/>
                             </div>
                             <div class="col-md-8">
                                <div class="card-body">
                                <div class="d-flex flex-row bd-highlight mb-3">
                                 <h2 class="text-start">Sample</h2>
                                 </div>

                                 <p class="text-start">
                                 <h5 class="text-start">A software engineer is a person who applies the principles of software engineering to design, develop, maintain, test, and evaluate computer software. The term programmer is sometimes used as a synonym, but may also lack connotations of engineering education or skills.</h5>
                                 </p>
                                 </div>
                                </div>
                    </div>
                    </div>
            </div>
            </div>
            <div>

        <div class="input-group w-50 p-3">
  <div class="input-group-prepend">
    <span class="input-group-text">Name</span>
  </div>
  <textarea class="form-control" aria-label="With textarea" rows="1"></textarea>
</div>
<div><p>
    <br/></p>
</div>

<div class="input-group w-90 p-3">
  <div class="input-group-prepend">
    <span class="input-group-text">Description</span>
  </div>
  <textarea class="form-control" aria-label="With textarea" rows="3"></textarea>
</div>
<div>
    <button>submit</button>
</div>


        </div>
            </div>
        );
    }
}
